var fs = require('fs');
const matchRe = /^move/
const labelRe = /^ 1 /
const blockRe = /^\[([A-Za-z])\] ?$/
const moveRe = /^move ([0-9]+) from ([0-9]+) to ([0-9]+)$/

const addToMap = (m, c) => {
  if (m.has(c)) {
    m.set(c, m.get(c)+1)
  } else {
    m.set(c, 1)
  }
}

const removeFromMap = (m, c) => {
  if (m.has(c)) {
    if (m.get(c) > 1) {
      m.set(c, m.get(c)-1)
    } else {
      m.delete(c)
    }
  } else {
    console.log(`Unexpected remove call for ${c} from ${[...m.entries()]}`)
  }
}

const indexOfNonRepeating = (input, n) => {
  const m = new Map();

  for (let i=0; i < input.length; i++) {
    if (i < n) {
      addToMap(m, input[i])
    } else {
      //console.log(`i:${i}  m:${[...m.entries()]}`)
      if (m.size >= n) {
        return i
      } else {
        removeFromMap(m, input[i-n])
        addToMap(m, input[i])
      }
    }
  }
  return -1
}

